require(['jquery', 'xwiki-meta'], function ($, xm) {
$(function(){
  var hiddenSpaces = getCookieHierarchy('hiddenSpaces').split(',');
  var hiddenPages = getCookieHierarchy('hiddenPages').split(',');
  function loadHierarchy() {
    $.ajax({
      url: wikiGetURL+"WebIDECode/ApplicationHierarchy?xpage=plain",
      type: "GET",
      datatype : "html",
      data : "action=view&editor=view&masterPage="+XWiki.currentSpace+"."+XWiki.currentPage+"&remotePage="+XWiki.currentSpace+"."+XWiki.currentPage+"&hiddenSpaces="+encodeURIComponent(hiddenSpaces)+"&hiddenPages="+encodeURIComponent(hiddenPages),
      contentType: "text/html",
    }).success(function(data){
      $('#hierarchyBlock').html(data);
      $('head').append('<script type="text/javascript" src="/xwiki/bin/jsx/WebIDECode/ApplicationHierarchy" defer="defer"></script>');
    });
  }
  function getCookieHierarchy(cname) {
    var name = XWiki.currentPage+"-"+cname + "=";
    var ca = document.cookie.split(';');
    for(var i=0; i<ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0)==' ') c = c.substring(1);
      if (c.indexOf(name) == 0) return c.substring(name.length,c.length);
    }
    return "";
  }
  loadHierarchy();

});
});

