property.code:
require(['jquery'], function ($) {
$(function(){
  var NESTED_SPACES = checkNestedSpaces();
  var currentPage = encodeURIComponent(XWiki.currentPage);
  var currentSpace = encodeURIComponent(XWiki.currentSpace);
  var currentPageEscaped = encodeURIComponent(XWiki.currentPage.replace(/\\/g, '\\\\').replace(/\./g, '\\\.'));
  var currentSpaceEscaped = encodeURIComponent(getSpacesEscaped(XWiki.currentSpace));
  var wikiViewURL = '/'+XWiki.webapppath+''+XWiki.servletpath+'view/';
  var bootstrap = (typeof($.fn.modal) !== 'undefined'); // Modal won't work without bootstrap
  if(window.docviewurl.indexOf('/'+XWiki.currentSpace+'/') >= 0) {
    wikiViewURL = window.docviewurl.substring(0,window.docviewurl.indexOf('/'+XWiki.currentSpace+'/')+1);
  }
  var wikiEditURL = '/'+XWiki.webapppath+''+XWiki.servletpath+'edit/';
  if(window.docediturl.indexOf('/'+XWiki.currentSpace+'/') >= 0) {
    wikiGetURL = window.docediturl.substring(0,window.docediturl.indexOf('/'+XWiki.currentSpace+'/')+1);
  }
  var hashURL = window.location.hash.substring(1).split('&!!');
  // Determine if that page is part of a project. Add the XWebIDE link in the Actions menu if that's the case and the "W" shortcut to use the XWebIDE editor.
  $.ajax({
    url: wikiViewURL+"WebIDECode/ApplicationCheck?xpage=plain&outputSyntax=plain&doc="+currentSpaceEscaped+"."+currentPageEscaped,
    type: "GET",
    contentType: "application/json",
  }).success(function(data){
    if(data.error == "-1") { // Type = 1, Is the page part of a project?
      if(XWiki.contextaction == 'edit' || XWiki.contextaction == 'view') {
        if($('#tmEdit .submenu .submenuseparator').length) { //Colibri Skin
          $('#tmEdit .submenu .submenuseparator').before('<span class="submenuitem "><a href="#" id="tmEditWebIDE" class="tmEditXWebIDE" title="Edit '+data.documentName+' in XWebIDE">XWebIDE</a></span>');
          $('.tmEditXWebIDE').css({
            "backgroundImage": "url('/"+XWiki.webapppath+"resources/icons/silk/folder_page.png')"
          });
        }
        else { //Flamingo
          $('#tmEdit .dropdown-menu .divider:first').before('<li class=""><a href="#" id="tmEditWebIDE" class="tmEditXWebIDE" title="Edit '+data.documentName+' in XWebIDE"><span class="glyphicon  glyphicon-folder-open"></span> XWebIDE</a></li>');
        }
        $('.tmEditXWebIDE').click(function(e) {
          e.preventDefault();
          loadMasterPage(1);
        });
      }
      shortcut.add("W", function() {
        loadMasterPage(1);
      } ,{'disable_in_input':'true'});

      // Display the hierarchy if the hash is in the URL
      if(XWiki.contextaction == 'edit' && window.location.hash && !hierarchyStatus) {
        if(hashURL[0] == '!XWebIDE') {
          displayHierarchy(data.projectData[0].masterSpace, data.projectData[0].masterName);
        }
      }
    }

    if(data.sheetError == "-1") { // Type = 2, Does the page use a sheet which is part of a project?
      if(XWiki.contextaction == 'edit' || XWiki.contextaction == 'view') {
        if($('#tmEdit .submenu .submenuseparator').length) { //Colibri Skin
          $('#tmEdit .submenu .submenuseparator').before('<span class="submenuitem "><a href="#" id="tmEditWebIDE" class="tmEditXWebIDESheet" title="Edit the sheet of '+data.documentName+' in XWebIDE">XWebIDE (Sheet)</a></span>');
          $('.tmEditXWebIDESheet').css({
            "backgroundImage": "url('/"+XWiki.webapppath+"resources/icons/silk/folder_page.png')"
          });
        }
        else { //Flamingo
          $('#tmEdit .dropdown-menu .divider').before('<li class=""><a href="#" id="tmEditWebIDE" class="tmEditXWebIDESheet" title="Edit the sheet of '+data.documentName+' in XWebIDE"><span class="glyphicon  glyphicon-folder-open"></span> XWebIDE (Sheet)</a></li>');
        }
        $('.tmEditXWebIDESheet').click(function(e) {
          e.preventDefault();
          loadMasterPage(2);
        });
      }
      shortcut.add("Shift+W", function() {
        loadMasterPage(2);
      } ,{'disable_in_input':'true'});
    }

    function loadMasterPage(checkType) {
      if(checkType == 1 && data.size == "1") {
        window.location.href = wikiViewURL+encodeURI(getURLSpacesString(unescape(data.projectData[0].masterSpace)))+'/'+encodeURIComponent(unescape(data.projectData[0].masterName))+'#!XWebIDE&!!wiki&!!'+currentPage+'&!!'+currentSpace;
      }
      else if(checkType == 2 && data.sheetSize == "1") {
        window.location.href = wikiViewURL+encodeURI(getURLSpacesString(unescape(data.sheet[0].sheetProjectData[0].sheetMasterSpace)))+'/'+encodeURIComponent(unescape(data.sheet[0].sheetProjectData[0].sheetMasterName))+'#!XWebIDE&!!wiki&!!'+encodeURIComponent(unescape(data.sheet[0].sheetDocumentName))+'&!!'+encodeURIComponent(unescape(data.sheet[0].sheetDocumentSpace))+'';
      }
      else if(parseInt(data.size) > 1 || parseInt(data.sheetSize) > 1){
        var message = 'That page is in several projects. Which one do you want to edit?';
        var projectURLList = '';
        if(checkType == 1) {
          $.each(data.projectData, function(key, value) {
            projectURLList += '<a href="'+wikiViewURL+getURLSpacesString(value.masterSpace)+'/'+value.masterName+'#!XWebIDE&!!wiki&!!'+currentPage+'&!!'+currentSpace+'" id="tmEditWebIDE" class="tmEditXWebIDE" title="Edit '+data.documentName+' in XWebIDE as part of '+value.project+'">Edit <strong>'+data.documentName+'</strong> in project <strong>'+value.project+'</strong></a><br />';
          });
        }
        else if(checkType == 2) {
          message = 'That page uses sheets from several projects. Which project do you want to edit?';
          $.each(data.sheet, function(keySheet, valueSheet) {
            $.each(valueSheet.sheetProjectData, function(key, value) {
              projectURLList += '<a href="'+wikiViewURL+getURLSpacesString(value.sheetMasterSpace)+'/'+value.sheetMasterName+'#!XWebIDE&!!wiki&!!'+valueSheet.sheetDocumentName+'&!!'+valueSheet.sheetDocumentSpace+'" id="tmEditWebIDE" class="tmEditXWebIDE" title="Edit '+valueSheet.sheetDocumentName+' in XWebIDE as part of '+value.sheetProject+'">Edit <strong>'+valueSheet.sheetDocumentName+'</strong> in project <strong>'+value.sheetProject+'</strong></a><br />';
            });
          });
        }
        $('#modalProjectSelection').remove();
        $('<div id="modalProjectSelection" class="modal fade" role="dialog"><div class="modal-dialog"><div class="modal-content"><div class="modal-header"><button type="button" class="close" data-dismiss="modal">&times;</button><h4 class="modal-title">XWebIDE editor</h4></div><div class="modal-body"><p>'+message+'</p><p>'+projectURLList+'</p></div><div class="modal-footer"><button type="button" class="btn btn-default" data-dismiss="modal">Close</button></div></div></div></div>').appendTo('body');
        if(bootstrap) {
          $('#modalProjectSelection').modal('show');
        }
        else {
          $('button[data-dismiss="modal"]').click(function() {
            $('#modalProjectSelection').hide();
          });
          $('#modalProjectSelection').css({
            "width" : "40%",
            "border" : "1px solid black",
            "backgroundColor" : "#CCCCCC",
            "borderRadius" : "5px",
            "position" : "absolute",
            "left" : "30%",
            "top" : "20%",
            "padding" : "10px"
          });
          $('.modal-header .close').css({
            "float" : "right",
            "position" : "absolute",
            "right" : "5px",
            "top" : "5px"
          });
          $('.modal-header .modal-title').css({
            "marginTop" : "0px"
          });
        }
      }
    }

  });

  var hierarchyStatus = false;
  function displayHierarchy(masterSpace, masterName) {
    if(!hierarchyStatus && !document.getElementById('hierarchyBlock')) {
      hierarchyStatus = true;
      var visibleSpaces = getCookieHierarchy(encodeURIComponent(masterName), 'visibleSpaces');
      var visiblePages = getCookieHierarchy(encodeURIComponent(masterName), 'visiblePages');
      var wikiGetURL = '/'+XWiki.webapppath+''+XWiki.servletpath+'get/';
      var remotePageName = currentPageEscaped;
      var remoteSpaceName = currentSpaceEscaped;
      var currentTree = getCookieHierarchy(encodeURIComponent(masterName), 'currentTree') || 'logical';
      if(hashURL.size() == 3) {
        var sourcePage = hashURL[1];
        var sourceSpace = hashURL[2];
        remotePageName = encodeURIComponent(sourcePage.replace(/\\/g, '\\\\').replace(/\./g, '\\\.'));
        remoteSpaceName = encodeURIComponent(sourceSpace.replace(/\\/g, '\\\\').replace(/\./g, '\\\.'));
        window.location.hash = '#!XWebIDE';
      }
      if(window.docgeturl.indexOf('/get/') != 0) wikiGetURL = window.docgeturl.substring(0,window.docgeturl.indexOf('/get/')+5);
      $.ajax({
        url: wikiGetURL+"WebIDECode/ApplicationHierarchy?xpage=plain&action="+XWiki.contextaction+"&editor="+XWiki.editor+"&masterPage="+encodeURI(masterSpace)+"."+encodeURI(masterName)+"&remotePage="+remoteSpaceName+"."+remotePageName+"&visibleSpaces="+encodeURIComponent(visibleSpaces)+"&visiblePages="+encodeURIComponent(visiblePages)+"&treeview="+currentTree,
        type: "GET",
        contentType: "text/plain",
      }).success(function(data){
        var mainBlock = '';
        if(XWiki.editor == "class"){
          mainBlock = '#propupdate';
          $(mainBlock).before('<div id="hierarchyBlock" >'+data+'</div>');
          $('head').append('<link rel="stylesheet" type="text/css" href="/'+XWiki.webapppath+''+XWiki.servletpath+'ssx/WebIDECode/ApplicationHierarchy"/>');
          $('head').append('<script type="text/javascript" src="/'+XWiki.webapppath+''+XWiki.servletpath+'jsx/WebIDECode/ApplicationHierarchy" defer="defer"></script>');
          $(mainBlock).css({
            "overflow" : "auto",
            "display" : "block",
            "padding" : "20px"
          });
        }
      });
    }
  }

  function getCookieHierarchy(masterName, cname) {
    var name = masterName+"-"+cname + "=";
    var ca = document.cookie.split(';');
    for(var i=0; i<ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0)==' ') c = c.substring(1);
      if (c.indexOf(name) == 0) return c.substring(name.length,c.length);
    }
    return "";
  }

  /**
   * Check if Nested spaces are available if that version of XWiki
   * @return boolean
   */
  function checkNestedSpaces() {
    if(XWiki.EntityReferenceTreeNode)
      return true;
    else
      return false;
  }

  /**
   * @param spaces The string describing spaces with XWiki classic format 'A.B'
   * @return The string describing spaces with XWiki REST format 'spaces/A/spaces/B'
   */
  function getRestSpacesString(spaces) {
    if(NESTED_SPACES) {
      var spacesEnc = spaces.replace(/%5C\./g, '%5C%2E').replace(/\\\./g, '%5C%2E');
      var spacesList = spacesEnc.split('.');
      var restFormat = '';
      for(var i=0; i<spacesList.length; i++) {
        restFormat += 'spaces/'+spacesList[i].replace('%5C%2E', '\.')+'/';
      }
      return restFormat.substring(0, restFormat.length - 1);
    }
    else return 'spaces/'+spaces+'';
  }

  /**
   * @param spaces The string describing spaces with XWiki classic format 'A.B'
   * @return The string describing spaces with XWiki URL format 'A/B'
   */
  function getURLSpacesString(spaces) {
    if(NESTED_SPACES) {
      var spacesEsc = spaces.replace(/%5C\./g, '%5C%2E').replace(/\\\./g, '%5C%2E');
      var spacesList = spacesEsc.split('.');
      var restFormat = '';
      for(var i=0; i<spacesList.length; i++) {
        restFormat += spacesList[i].replace('%5C%2E', '\.')+'/';
      }
      return restFormat.substring(0, restFormat.length - 1);
    }
    else return spaces;
  }

  /**
   * @param spaces The string describing a space with XWiki classic format 'A.B' (without nested space)
   * @return The string describing escaped space name 'A\.B'
   */
  function getSpacesEscaped(spaces) {
    if(NESTED_SPACES) {
      return spaces;
    }
    else return spaces.replace('%5C', '%5C%5C').replace(/\\/g, '%5C%5C').replace(/\./g, '%5C.');
  }
});
});
